function linkProducts(copy) {
    
    if (!copy) return "";
    
    var url = "https://store.dandelionchocolate.com/#Pantry-and-Baking";
    var link = "<a class='product-link' href='" + url + "' target='_blank'>"
    
    copy = copy.replace(/cocoa nibs/gi, link + "cocoa nibs</a>");
    
    copy = copy.replace(/70% chef\'s chocolate/gi, link + "70% chef's chocolate</a>");
    copy = copy.replace(/chef\'s chocolate/gi, link + "chef's chocolate</a>");
    
    copy = copy.replace(/chef\'s ground chocolate/gi, link + "chef's ground chocolate</a>");

    copy = copy.replace(/large chips/gi, link + "large chips</a>");
    copy = copy.replace(/70% LARGE CHOCOLATE CHIPS/gi, link + "70% large chocolate chips</a>");

    copy = copy.replace(/100% LARGE CHOCOLATE CHIPS/gi, link + "100% large chocolate chips</a>");


    return copy;
}

function formatFractions(copy) {
    
    if (!copy) return "";

    copy = copy.replace(/1\/2/gi, '½');
    copy = copy.replace(/1\/3/gi, '⅓');
    copy = copy.replace(/2\/3/gi, '⅔');
    copy = copy.replace(/1\/4/gi, '¼');
    copy = copy.replace(/3\/4/gi, '¾');
    copy = copy.replace(/1\/5/gi, '⅕');
    copy = copy.replace(/2\/5/gi, '⅖');
    copy = copy.replace(/3\/5/gi, '⅗');
    copy = copy.replace(/4\/5/gi, '⅘');
    copy = copy.replace(/1\/6/gi, '⅙');
    copy = copy.replace(/5\/6/gi, '⅚');
    copy = copy.replace(/1\/7/gi, '⅐');
    copy = copy.replace(/1\/8/gi, '⅛');
    copy = copy.replace(/3\/8/gi, '⅜');
    copy = copy.replace(/5\/8/gi, '⅝');
    copy = copy.replace(/7\/8/gi, '⅞');
    copy = copy.replace(/1\/9/gi, '⅑');
    copy = copy.replace(/1\/10/gi, '⅒');

    return copy;
}

//swap out PLUS for +
function formatUnits(copy) {
    
    if (!copy) return "";

    copy = copy.replace('PLUS', '+<br>');
    copy = copy.replace('plus', '+<br>');
    return copy;
}
 
function writeRecipe(recipe) {

    var page = document.getElementById('rd-page');
    var template = document.getElementById('recipe-template');
    document.getElementById('recipe-image').style.backgroundImage = "url('" + recipe.image + "')";

    //var image = document.getElementById('recipe-image');
    //writeRecipeImage();
    
    var clone = template.cloneNode(true);
    clone.id = recipe.id;

    //MAIN TITLE
    clone.getElementsByClassName('headline')[0].innerHTML = recipe.headline;
        
    //SUBHEADER
    if (clone.getElementsByClassName('author')[0] && recipe.author)
        clone.getElementsByClassName('author')[0].innerHTML = recipe.author;
    
    if (clone.getElementsByClassName('team')[0] && recipe.team)
        clone.getElementsByClassName('team')[0].innerHTML = 'from ' + recipe.team;
    
    if (clone.getElementsByClassName('published')[0] && recipe.textDate)
        clone.getElementsByClassName('published')[0].innerHTML = recipe.textDate;
        
    //BLOCKQUOTE
    if (clone.getElementsByClassName('quote')[0] && recipe.text)
        clone.getElementsByClassName('quote')[0].innerHTML = recipe.text;

    //START OF RECIPE
    if (clone.getElementsByClassName('title')[0] && recipe.name)
        clone.getElementsByClassName('title')[0].innerHTML = recipe.name;
    
    if (clone.getElementsByClassName('time-estimate')[0] && recipe.timeRequired)
        clone.getElementsByClassName('time-estimate')[0].innerHTML = recipe.timeRequired;
 
    if (clone.getElementsByClassName('yield')[0])
        clone.getElementsByClassName('yield')[0].innerHTML = recipe.yield;

    writeIngredients(clone, recipe.ingredients);
    writeSteps(clone, recipe.steps);

    clone.style.display = 'block';//make visible before adding to dom
    page.appendChild(clone);
}


function writeIngredients(recipeObj, ingredients) {
    
    var html = "";
    
    for (var i = 0; i < ingredients.length; i++) {
        
        var position = parseInt(ingredients[i].position);
        
        switch (ingredients[i].rowType) {
            case "header":
                html += '<div class="ingredient-header">' + ingredients[i].name + '</div>';
                html += '<div class="three-columns ingredients-legend-row">';
                html += '<div class="columnitem">UNITS</div> \
                <div class="columnitem">WEIGHT</div> \
                <div class="columnitem lastcolumn"><span style="color: #ffffff;">–</span></div></div>';
            break;
        case "ingredient":
            html += '<div class="three-columns ingredient-row ';
            html += (position % 2) ? 'row-lowlight' : 'row-highlight';//add zebra striping
            html += '">';
            html += '<div class="columnitem"><p style="text-align: center;">';
            html += '' + (formatFractions(formatUnits(ingredients[i].units)) || '-') + '</p></div>';
            html += '<div class="columnitem">' + (ingredients[i].weight || '-') + '</div>';
            html += '<div class="columnitem lastcolumn ingredient-cell"><p style="text-align: left;">';
            html += linkProducts(formatFractions(ingredients[i].name));
            html += '</p></div></div>';
            break; 
        default:
            console.log("udi rowType = " + recipeObj.id + '-' + ingredients[i].rowType);
            break;
        }
        
    }

    recipeObj.getElementsByClassName('ingredients')[0].innerHTML = html;
}

function writeSteps(recipeObj, steps) {
    
    var html = "";
    
    for (var i = 0; i < steps.length; i++) {
        
        switch (steps[i].rowType) {
            case "header":
                html += '<div class="step-header">' + steps[i].text + '</div>';
                break;
            case "step":
                var number = parseInt(steps[i].number);

                html += '<div class="step-row">';
                html += '<div class="step-number"><div class="numberCircle"><p>' + number + '</p></div></div>';
                html += '<div class="step-text">' + formatFractions(steps[i].text) + '</div></div>';
                if ((i == steps.length - 1) || (steps[i + 1].rowType != 'header')) {// don't add a header when it's the last step before another header
                    html += '<div class="step-divider"><hr></div>';
                }
                break; 
        default:
            console.log("uds rowType = " + recipeObj.id + '-' + steps[i].rowType);
            break;
        }
        
    }

    recipeObj.getElementsByClassName('steps')[0].innerHTML = html;
}

